(function ()
{
    'use strict';

    angular.module('app.index', []);
})();

(function ()
{
    'use strict';

    angular.module('app.index')
           .controller('IndexController', IndexController);

    IndexController.$inject = ['api', '$sce'];



    function IndexController(api, $sce)
    {
        var vm = this;

        vm.user = [];
        vm.fragment = [];
        vm.test = [];
        vm.postInfo = [];

        vm.preview = preview;
        vm.create = create;

        getUser();
        postInfo();
        /*
         |--------------------------------------------------------------------------------------------------------------
         | Fragments Create
         |--------------------------------------------------------------------------------------------------------------
         */

        function postInfo()
        {
            api.postInfo().then(function (data)
            {
                vm.postInfo = data;
            });
        }

        function create()
        {
            var data = {
                url: vm.fragment.url,
                user_id: 28,
                start: vm.fragment.start,
                end: vm.fragment.end
            };

            api.postFragmentCreate(data).then(function (data)
            {
                vm.create = data;
            });

        }

        /*
         |--------------------------------------------------------------------------------------------------------------
         | Fragments
         |--------------------------------------------------------------------------------------------------------------
         */

        function preview()
        {
            var data = {
                url: vm.fragment.url,
                start: vm.fragment.start,
                end: vm.fragment.end
            };

            api.getEmbedUrl(data).then(function (data)
            {
                vm.fragment.embed_url = $sce.trustAsResourceUrl(data);
            });
        }

        /*
         |--------------------------------------------------------------------------------------------------------------
         | Users
         |--------------------------------------------------------------------------------------------------------------
         */

        function getUser()
        {
            api.getUser().then(function (data)
            {
                vm.user = data;
            });
        }
    }
})();

(function ()
{
    'use strict';

    angular.module('app.index')
           .factory('api', api);

    api.$inject = ['$http'];

    function api($http)
    {
        return {
            "getEmbedUrl": getEmbedUrl,
            "getUser": getUser,
            "postFragmentCreate": postFragmentCreate,
            "postInfo": postInfo
        };

        function postInfo(data)
        {
            return $http.post('/api/fragments/info/358', data).then(function (response)
            {
                return response.data;
            });
        }


        function getEmbedUrl(data)
        {
            return $http.post('/api/fragments/embed_url', data).then(function (response)
            {
                return response.data;
            });
        }

        function postFragmentCreate(data)  {
            return $http.post('/api/fragments/create', data).then(function (response)
            {
                return response.data;
            });

        }

        function getUser()
        {
            return $http.get('/api/users/me').then(function (response)
            {
                return response.data;
            });
        }
    }
})();

(function()
{
    'use strict';

    angular.module('app.index')
           .directive('appEmbed', appEmbed);

    function appEmbed()
    {
        return {
            restrict: 'A',
            link: link
        };

        function link(scope, element, attributes)
        {
            $(element).embed();
        }
    }
})();

